        text: 'Back',
        action: tour.back,
        classes: 'shepherd-button-secondary'
      });
    } else if (steps.length > 1) {
      buttons.push({
        text: 'Exit',
        action: tour.cancel,
        classes: 'shepherd-button-secondary'
      });
    }

