      expect(destroyCalled, 'setupElements method called destroy with element set').toBeTruthy();
    });

    it('calls destroy on the tooltip if it already exists', () => {
      const step = new Step();
      let destroyCalled = false;
      step.tooltip = {
        destroy() { destroyCalled = true; }
      };
      step.setupElements();
      expect(destroyCalled, 'setupElements method called destroy on the existing tooltip').toBe(true);
    });

    it('calls bindAdvance() if advanceOn passed', () => {
      const step = new Step({
        next: () => true
      }, {
        advanceOn: '.click-test test'
      });
      const bindFunction = spy(step, 'bindAdvance');
      step.setupElements();

      expect(bindFunction.called).toBeTruthy();
    });
  });

  describe('scrollTo()', () => {
    it('calls the scroll native method', () => {
      const div = document.createElement('div');
      let handlerCalled = false;
      div.classList.add('scroll-test');
      document.body.appendChild(div);
      const step = new Step('test', {
        attachTo: { element: '.scroll-test', on: 'center' }
      });
      div.scrollIntoView = () => handlerCalled = true;

      step.scrollTo();
      expect(handlerCalled).toBeTruthy();
    });

    it('calls the custom handler', () => {
      let handlerAdded = false;
      const step = new Step('test', {
        scrollToHandler: () => handlerAdded = true
      });

      step.scrollTo();
      expect(handlerAdded).toBeTruthy();
    });
  });

  describe('setOptions()', () => {
    it('calls event handlers passed in as properties to the `when` option', () => {
      let whenCalled = false;
      const step = new Step('test', {
        when: {
          destroy: () => whenCalled = true
        }
      });

      step.destroy();
      expect(whenCalled).toBeTruthy();
    });
  });

  describe('getTour()', () => {
    it('returns the tour value', () => {
      const step = new Step(new Shepherd.Tour());

      expect(step.getTour() instanceof Shepherd.Tour).toBeTruthy();
    });

  });

  describe('_addContent()', () => {
    it('adds plain text to the content', () => {
      const content = document.createElement('div');
      const step = new Step();
      step.options.text = 'I am some test text.';

      step._addContent(content);

      expect(content.querySelector('.shepherd-text p').innerHTML).toBe('I am some test text.');
    });

    it('maps mutiple strings to paragraphs', () => {
      const content = document.createElement('div');
      const step = new Step();
      step.options.text = ['I am some test text.', 'I am even more test text.'];

      step._addContent(content);
      expect(content.querySelectorAll('.shepherd-text p').length).toBe(2);
      expect(step.options.text.join(' '))
        .toBe(Array.from(content.querySelectorAll('.shepherd-text p')).map((text) => text.innerHTML).join(' '));
    });

    it('applies HTML element directly to content', () => {
      const content = document.createElement('div');
      const text = document.createElement('p');
      const step = new Step();
      text.innerHTML = 'I am some test text.';
      step.options.text = text;

      step._addContent(content);

      expect(content.querySelector('.shepherd-text p').innerHTML).toBe('I am some test text.');
    });

    it('applies the text from a function', () => {
      const content = document.createElement('div');
      const step = new Step();
      step.options.text = () => 'I am some test text.';

      step._addContent(content);

      expect(typeof step.options.text === 'function').toBeTruthy();
      expect(content.querySelector('.shepherd-text p').innerHTML).toBe('I am some test text.');
    });
  });

  describe('_addButtons', () => {
    it('renders no buttons if an empty array is passed to `options.buttons`', () => {
      const content = document.createElement('div');
      const step = new Step();

      step.options.buttons = [];

      step._addButtons(content);

      expect(content.children.length).toBe(0);
    });

    it('renders no buttons if nothing is passed to `options.buttons`', () => {
      const content = document.createElement('div');
      const step = new Step();

      step._addButtons(content);

      expect(content.children.length).toBe(0);
    });

    it('renders buttons for each item passed to `options.buttons`', () => {
      const content = document.createElement('div');
      const step = new Step();

      step.options.buttons = [
        defaultButtons.cancel,
        defaultButtons.next
      ];

      step._addButtons(content);

      expect(content.children.length).toBe(1);

      const buttonContainer = content.querySelector('.shepherd-buttons');

      expect(buttonContainer instanceof HTMLElement).toBe(true);

      const buttons = buttonContainer.querySelectorAll('.shepherd-button');

      expect(buttons.length).toBe(2);
    });
  });
});

import _ from 'lodash';
import { stub } from 'sinon';
import Shepherd from '../../src/js/shepherd.js';
import { Step } from '../../src/js/step.js';
import tippy from 'tippy.js';
import { defaults as tooltipDefaults } from '../../src/js/utils/tooltip-defaults';
import { spy } from 'sinon';

// since importing non UMD, needs assignment
window.Shepherd = Shepherd;

const DEFAULT_STEP_CLASS = 'shepherd-step-tooltip';

describe('Tour | Top-Level Class', function() {
  let instance, shouldShowStep;

  const defaultStepOptions = {
    classes: DEFAULT_STEP_CLASS,
    scrollTo: true
  };

  beforeEach(() => {
    tippy.setDefaults({ duration: 0, delay: 0 });
  });

  afterEach(() => {
    instance.complete();
  });

  describe('constructor', function() {
    it('creates a new tour instance', function() {
      instance = new Shepherd.Tour({ defaultStepOptions });

      expect(instance instanceof Shepherd.Tour).toBe(true);
    });

    it('returns the default options on the instance', function() {
      instance = new Shepherd.Tour({ defaultStepOptions });

      expect(instance.options.defaultStepOptions).toEqual({
        classes: DEFAULT_STEP_CLASS,
        scrollTo: true
      });
    });

    it('sets the correct bindings', function() {
      instance = new Shepherd.Tour({ defaultStepOptions });

      const bindings = Object.keys(instance.bindings);
      const tourEvents = ['complete', 'cancel', 'start', 'show', 'active', 'inactive'];
      // Check that all bindings are included
      const difference = _.difference(tourEvents, bindings);
      expect(difference.length, 'all tour events bound').toBe(0);
    });

    it('sets defaults for tippy', function() {
      const tourSpy = spy(Shepherd.Tour.prototype, '_setTooltipDefaults');
      const tippySpy = spy(tippy, 'setDefaults');

      expect(tourSpy.callCount).toBe(0);
      expect(tippySpy.callCount).toBe(0);

      instance = new Shepherd.Tour({ defaultStepOptions });

      expect(tourSpy.callCount).toBe(1);
      expect(tippySpy.callCount).toBe(1);
      expect(tippySpy.calledWith(tooltipDefaults)).toBe(true);
    });

    it('generates a unique `id` property, optionally based upon the `tourName` option', function() {
      const instance1 = new Shepherd.Tour();
      const instance2 = new Shepherd.Tour({ tourName: 'select-avatar' });

      expect(instance1.id.startsWith('tour--')).toBe(true);
      expect(instance2.id.startsWith('select-avatar--')).toBe(true);

      const uniqueId1 = instance1.id.split('--')[1];
      const uniqueId2 = instance2.id.split('--')[1];

      expect(uniqueId1).not.toBe(uniqueId2);
    });
  });

  describe('methods', () => {
    beforeEach(() => {
      shouldShowStep = false;

      instance = new Shepherd.Tour({
        defaultStepOptions
      });

      instance.addStep('test', {
        id: 'test',
        title: 'This is a test step for our tour'
      });

      instance.addStep('skipped-step', {
        classes: 'skipped',
        id: 'skipped-step',
        title: 'This step should be skipped',
        showOn() {
          return shouldShowStep;
        }
      });

      instance.addStep('test2', {
        id: 'test2',
        title: 'Another Step'
      });

      instance.addStep('test3', {
        id: 'test3',
        title: 'Yet, another test step'
      });
    });

    describe('.addStep()', function() {
      it('adds tour steps', function() {
        expect(instance.steps.length).toBe(4);
        expect(instance.getById('test').options.classes, 'classes passed to step options').toBe(DEFAULT_STEP_CLASS);
      });

      it('adds steps with only one arg', function() {
        const step = instance.addStep({
          id: 'one-arg'
        });

        expect(instance.steps.length).toBe(5);
        expect(step.id, 'id applied to step with just one arg').toBe('one-arg');
      });

      it('adds steps that are already Step instances', function() {
        const step = instance.addStep(new Step(instance, {
          id: 'already-a-step'
        }));

        expect(instance.steps.length).toBe(5);
        expect(step.id, 'id applied to step instance').toBe('already-a-step');
        expect(step.tour, 'tour is set to `this`').toBe(instance);
      });
    });

    describe('.getById()', function() {
      it('returns the step by ID with the right title', function() {
        expect(instance.steps.length).toBe(4);
        expect(instance.getById('test3').options.title).toBe('Yet, another test step');
      });
    });

    describe('.start()', function() {
      it('starts a tour that is the current active', function() {
        instance.start();

        expect(instance).toBe(Shepherd.activeTour);
      });

      it('adds the current tour\'s "id" property to the body as a data attribute', function() {
        instance.id = 'test-id';
        instance.start();

        expect(document.body.hasAttribute('data-shepherd-active-tour')).toBe(true);
        expect(document.body.getAttribute('data-shepherd-active-tour')).toBe('test-id');
      });
    });

    describe('.getCurrentStep()', function() {
      it('returns the currently shown step', function() {
        instance.start();
        expect(instance.getCurrentStep().id).toBe('test');
      });
    });

    describe('.hide()', function() {
      it('hides the current step', () => {
        const firstStep = instance.steps[0];
        const hideStepSpy = spy(firstStep, 'hide');

        expect(firstStep.isOpen()).toBe(false);

        instance.start();

        expect(firstStep.isOpen()).toBe(true);

        instance.hide();

        expect(firstStep.isOpen()).toBe(false);
        expect(hideStepSpy.callCount).toBe(1);
      });
    });

    describe('isActive', function() {
      it('computes whether or not `Shepherd.activeTour` equals the instance', function() {
        Shepherd.activeTour = '';
        expect(instance.isActive()).toBe(false);

        Shepherd.activeTour = instance;
        expect(instance.isActive()).toBe(true);

        Shepherd.activeTour = '';
        expect(instance.isActive()).toBe(false);
      });
    });

    describe('.next()/.back()', function() {
      it('goes to the next/previous steps', function() {
        instance.start();
        instance.next();
        expect(instance.getCurrentStep().id).toBe('test2');
        instance.back();
        expect(instance.getCurrentStep().id).toBe('test');
      });

      it('next completes tour when on last step', function() {
        let completeFired = false;
        instance.on('complete', () => {
          completeFired = true;
        });

        instance.start();
        instance.show('test3');
        expect(instance.getCurrentStep().id).toBe('test3');
        instance.next();
        expect(completeFired, 'complete is called when next is clicked on last step').toBeTruthy();
      });
    });

    describe('.cancel()', function() {
      it('shows confirm dialog when confirmCancel is true', function() {
        instance = new Shepherd.Tour({
          defaultStepOptions,
          confirmCancel: true,
          confirmCancelMessage: 'Confirm cancel?'
        });

        instance.addStep('test', {
          id: 'test',
          title: 'This is a test step for our tour'
        });

        let inactiveFired = false;
        instance.on('inactive', () => {
          inactiveFired = true;
        });

        const windowConfirmStub = stub(window, 'confirm');
        windowConfirmStub.returns(false);

        instance.start();
        expect(instance, 'activeTour is set to our tour').toBe(Shepherd.activeTour);
        instance.cancel();
        expect(windowConfirmStub.called, 'window confirm is called').toBeTruthy();
        expect(inactiveFired, 'tour still going, since confirm returned false').toBeFalsy();

        windowConfirmStub.returns(true);
        instance.cancel();
        expect(windowConfirmStub.called, 'window confirm is called').toBeTruthy();
        expect(inactiveFired, 'tour inactive, since confirm returned true').toBeTruthy();
      });

      it('tears down tour on cancel', function() {
        let inactiveFired = false;
        instance.on('inactive', () => {
          inactiveFired = true;
        });
        instance.start();
        expect(instance, 'activeTour is set to our tour').toBe(Shepherd.activeTour);
        instance.cancel();
        expect(Shepherd.activeTour, 'activeTour is torn down').toBeFalsy();
        expect(inactiveFired, 'inactive event fired').toBeTruthy();
      });

      it('triggers cancel event when cancel function is called', function() {
        let cancelFired = false;
        instance.on('cancel', () => {
          cancelFired = true;
        });

        instance.start();
        instance.cancel();
        expect(cancelFired, 'cancel event fired').toBeTruthy();
      });
    });

    describe('.complete()', function() {
      it('triggers complete event when complete function is called', function() {
        let completeFired = false;

        instance.on('complete', () => {
          completeFired = true;
        });

        instance.start();
        instance.complete();
        expect(completeFired, 'complete event fired').toBeTruthy();
      });

      it('calls `done()`', () => {
        const doneSpy = spy(instance, 'done');

        expect(doneSpy.callCount).toBe(0);

        instance.start();
        instance.complete();

        expect(doneSpy.callCount).toBe(1);
      });
    });

    describe('.done()', function() {
      it('tears down the active tour', function() {
        instance.start();

        expect(instance, 'activeTour is set to our tour').toBe(Shepherd.activeTour);

        instance.complete();

        expect(Shepherd.activeTour, '`activeTour` is torn down and removed from the `Shepherd` global').toBe(null);
      });

      it('removes any of its `Step` tooltip elements from the DOM', function() {
        const testStep = {
          id: 'element-removal-test',
          classes: 'element-removal-test',
          title: 'This is a test step for our tour'
        };

        instance.addStep(testStep);
        instance.start();
        instance.show('element-removal-test');

        expect(document.querySelector(`.element-removal-test`),
          'a step is rendered in the DOM after the tour starts')
          .toBeInTheDocument();

        instance.complete();

        expect(document.querySelector(`.element-removal-test`),
          'steps are removed from the DOM after the tour completes')
          .not.toBeInTheDocument();
      });

      it('fires the `inactive` event', function() {
        let inactiveFired = false;

        instance.on('inactive', () => {
          inactiveFired = true;
        });

        instance.start();

        expect(inactiveFired, 'inactive event does not fire before `complete()`').toBe(false);

        instance.complete();

        expect(inactiveFired, 'inactive event fires after `complete()`').toBe(true);
      });
    });

    describe('.removeStep()', function() {
      it('removes the step when passed the id', function() {
        instance.start();
        expect(instance.steps.length).toBe(4);
        instance.removeStep('test2');
        expect(instance.steps.length).toBe(3);
      });

      it('hides the step before removing', function() {
        let hideFired = false;
        instance.start();
        expect(instance.steps.length).toBe(4);
        const step = instance.getById('test');
        step.on('hide', () => {
          hideFired = true;
        });
        instance.removeStep('test');
        expect(instance.steps.length).toBe(3);
        expect(hideFired, 'hide is fired before step is destroyed').toBeTruthy();
      });
    });

    describe('.show()', function() {
      it('show short-circuits if next is not found', function() {
        let showFired = false;
        instance.start();
        instance.on('show', () => {
          showFired = true;
        });
        instance.show('not-a-real-key');
        expect(showFired, 'showFired is false because show short circuits').toBeFalsy();
      });

      it('showOn determines which steps to skip', function() {
        instance.start();
        expect(instance.getCurrentStep().id).toBe('test');
        instance.next();
        expect(instance.getCurrentStep().id).toBe('test2');
        expect(instance.getCurrentStep().id, 'step skipped because `showOn` returns false').not.toBe('skipped-step');
        instance.back();
        shouldShowStep = true;
        instance.next();
        expect(instance.getCurrentStep().id, 'step shown because `showOn` returns true').toBe('skipped-step');
      });

      it(`sets the instance on \`Shepherd.activeTour\` if it's not already set`, function() {
        const setupFuncSpy = spy(instance, '_setupActiveTour');
        Shepherd.activeTour = null;

        expect(setupFuncSpy.callCount).toBe(0);

        instance.start();

        expect(setupFuncSpy.callCount).toBe(1);
        expect(Shepherd.activeTour).toBe(instance);
      });
    });
  });
});

import { cleanupSteps, cleanupStepEventListeners } from '../../../src/js/utils/cleanup';

describe('Cleanup Utils', function() {
  // Create some elements to use to attach to
  const firstAttachElement = document.createElement('div');
  firstAttachElement.classList.add('first-attach-to');
  firstAttachElement.style.pointerEvents = 'none';
  document.body.appendChild(firstAttachElement);

  const secondAttachElement = document.createElement('div');
  secondAttachElement.classList.add('second-attach-to');
  secondAttachElement.style.pointerEvents = 'none';
  document.body.appendChild(secondAttachElement);

  const mockedTour = {
    steps: [
      {
        options: {
          attachTo: '.first-attach-to bottom',
          canClickTarget: false
        }
      },
      {
        options: {
          attachTo: '.second-attach-to bottom',
          canClickTarget: false
        }
      }
    ]
  };

  describe('cleanupSteps', function() {
    it('cleans up steps and sets pointer-events to auto', () => {
      cleanupSteps(mockedTour);

      expect(firstAttachElement).toHaveStyle('pointer-events: auto');
      expect(secondAttachElement).toHaveStyle('pointer-events: auto');
    });
  });

  describe('cleanupStepEventListeners', function() {
    it('removes listeners', () => {
      const mock = {
        _onScreenChange() {}
      };

      cleanupStepEventListeners.call(mock);

      expect(mock._onScreenChange).toBe(null);
    });
  });
});

import {
  elementIsHidden,
  getElementForStep
} from '../../../src/js/utils/dom';

describe('DOM Utils', function() {
  describe('elementIsHidden', function() {
    it('returns true when hidden', () => {
      const element = document.createElement('div');
      element.setAttribute('offsetHeight', 0);
      element.setAttribute('offsetWidth', 0);

      expect(elementIsHidden(element), 'evaluates to true when offset height and width are 0').toBeTruthy();
    });
  });

  describe('getElementForStep', function() {
    it('attachTo object - element is HTMLElement', () => {
      const element = document.createElement('div');
      const step = {
        options: {
          attachTo: {
            element,
            on: 'bottom'
          }
        }
      };

      expect(getElementForStep(step), 'returns element as is for HTMLElement').toEqual(element);
    });

    it('attachTo object - element is selector', () => {
      const element = document.createElement('div');
      element.classList.add('foo');
      document.body.appendChild(element);

      const step = {
        options: {
          attachTo: {
            element: '.foo',
            on: 'bottom'
          }
        }
      };

      expect(getElementForStep(step), 'returns element from selector').toEqual(element);
    });

    it('attachTo string', () => {
      const element = document.createElement('div');
      element.classList.add('foo');
      document.body.appendChild(element);

      const step = {
        options: {
          attachTo: '.foo bottom'
        }
      };

      expect(getElementForStep(step), 'returns element from selector').toEqual(element);
    });
  });
});

import {
  _parseAttachToOpts,
  parseShorthand
} from '../../../src/js/utils/general.js';

describe('Utils', function() {
  describe('_parseAttachToOpts', function() {
    it('attachTo as an object', function() {
      const attachTo = {
        element: '.foo',
        on: 'bottom'
      };
      expect(_parseAttachToOpts(attachTo), 'when attachTo already includes `element` and `on` return as is')
        .toEqual(attachTo);
      expect(_parseAttachToOpts({}), 'when attachTo does not include `element` and `on`, return null').toBeNull();
    });

    it('attachTo as a string', function() {
      let attachTo = '.foo bottom';
      expect(_parseAttachToOpts(attachTo), 'when attachTo is a string, return as object with `element` and `on`')
        .toEqual({ element: '.foo', on: 'bottom' });

      attachTo = '.foo notValid';
      expect(_parseAttachToOpts(attachTo), 'when `on` is not a valid direction, return null').toBe(null);
    });
  });

  describe('parseShorthand', function() {
    it('null or undefined', function() {
      expect(parseShorthand(null), 'null returns null').toBe(null);
      expect(parseShorthand(undefined), 'undefined returns undefined').toBe(undefined);
    });

    it('string of values', function() {
      const values = '.foo click';
      const { event, selector } = parseShorthand(values, ['selector', 'event']);
      expect(event, 'maps event from string to event prop').toBe('click');
      expect(selector, 'maps selector from string to selector prop').toBe('.foo');
    });
  });
});

import {
  closeModalOpening,
  positionModalOpening
} from '../../../src/js/utils/modal';

const svgNS = 'http://www.w3.org/2000/svg';

describe('Modal Utils', function() {
  describe('closeModalOpening', function() {
    it('sets the correct attributes when closed', () => {
      const element = document.createElementNS(svgNS, 'rect');
      element.setAttribute('x', 20);
      element.setAttribute('y', 20);
      element.setAttribute('width', '100%');
      element.setAttribute('height', '100%');

      closeModalOpening(element);

      expect(element.getAttribute('x'), 'x should be 0').toBe('0');
      expect(element.getAttribute('y'), 'y should be 0').toBe('0');
      expect(element.getAttribute('width'), 'width should be \'0\'').toBe('0');
      expect(element.getAttribute('height'), 'height should be \'0\'').toBe('0');
    });
  });

  describe('positionModalOpening', function() {
    it('sets the correct attributes when positioning modal opening', () => {
      const targetElement = document.createElement('div');
      targetElement.getBoundingClientRect = () => {
        return {
          x: 20,
          y: 20,
          width: 500,
          height: 250
        };
      };

      const svgElement = document.createElementNS(svgNS, 'rect');
      positionModalOpening(targetElement, svgElement);

      expect(svgElement.getAttribute('x'), 'x should be 20').toBe('20');
      expect(svgElement.getAttribute('y'), 'y should be 20').toBe('20');
      expect(svgElement.getAttribute('width'), 'width should be 500').toBe('500');
      expect(svgElement.getAttribute('height'), 'height should be 250').toBe('250');
    });
  });
});
